import './App.css';
import React, { useState, useRef, useEffect } from 'react';
import chair from './chair.jpg';
import gif from './giphy.gif';
const paypalRef1 = ""
const paypalRef2 = ""
const paypalRef3 = ""

class Example extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      count: 0
    };
  }

  componentDidMount() {
    document.title = `You clicked ${this.state.count} times`;
  }

  componentDidUpdate() {
    document.title = `You clicked ${this.state.count} times`;
  }

  render() {
    return (
      <div>
        <p>You clicked {this.state.count} times</p>
        <button onClick={() => this.setState({ count: this.state.count + 1 })}>
          Click me
        </button>
      </div>
    );
  }
}


function Product({ product }) {
  const [paidFor, setPaidFor] = useState(false);
  const [error, setError] = useState(null);
  const paypalRef1 = useRef();
  const paypalRef2 = useRef();
  const paypalRef3 = useRef();

  function handle(){
    console.log("handle")
  }

  useEffect((j) => {
    var ray = []
    for(var i=0; i < product.length; i++) {
      console.log(product[i].pp)
      ray.push(product)
      window.paypal
        .Buttons({
          createOrder: (data, actions) => {
            return actions.order.create({
              purchase_units: [
                {
                  description: product[j].description,
                  amount: {
                    currency_code: 'USD',
                    value: '0.01',
                  },
                },
              ],
            });
          },
          onApprove: async (data, actions) => {
            const order = await actions.order.capture();
            setPaidFor(true);
            console.log("order approve" + order);
            console.log(data)
          }
        }).render(product[i].pp);
        console.log(i)
        console.log(ray)
      }
  }, '#paypall-button-container');

  if (paidFor) {
    return (
      <div>
        <h1>Congrats, you just bought {product.name}!</h1>
        <img alt={product.description} src={gif} />
      </div>
    );
  }

  return (
  product.map(item => (
    <div>
        <h1>{item.description} for ${item.price} </h1>
        <img alt={item.description} src={item.image} width="200" />
        {item.pp}
        <div ref={(node) => {item.pp = node;}}  onClick={handle}/>
    </div>
  ))
  );
}


function App() {
  const product = [
    {
      price: 0.01,
      name: 'stock 1',
      description: '1',
      image: chair,
      pp: paypalRef1,
      quantity: 1,
    },
    {
      price: 0.01,
      name: 'stock 2',
      description: '2',
      image: chair,
      pp: paypalRef2,
      quantity: 1,
    },
    {
      price: 0.01,
      name: 'stock 2',
      description: '3',
      image: chair,
      pp: paypalRef3,
      quantity: 1,
    }
  ];

  return (
    <div className="App">
      <Product product={product} />
    </div>
  );
}



export default App;
