import './App.css';
import React, { useState, useRef, useEffect } from 'react';
import chair from './chair.jpg';
import gif from './giphy.gif';
const paypalRef1 = ""
const paypalRef2 = ""
const paypalRef3 = ""

class Example extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      count: 0
    };
  }

  componentDidMount() {
    document.title = `You clicked ${this.state.count} times`;
  }

  componentDidUpdate() {
    document.title = `You clicked ${this.state.count} times`;
  }

  render() {
    return (
      <div>
        <p>You clicked {this.state.count} times</p>
        <button onClick={() => this.setState({ count: this.state.count + 1 })}>
          Click me
        </button>
      </div>
    );
  }
}


function Product({ product }) {
  const [paidFor, setPaidFor] = useState(false);
  const [error, setError] = useState(null);
  const paypalRef1 = useRef();
  const paypalRef2 = useRef();
  const paypalRef3 = useRef();

  useEffect(() => {
    window.paypal
      .Buttons({
        createOrder: (data, actions) => {
          return actions.order.create({
            purchase_units: [
              {
                description: product.description,
                amount: {
                  currency_code: 'USD',
                  value: product.price,
                },
              },
            ],
          });
        },
        onApprove: async (data, actions) => {
          const order = await actions.order.capture();
          setPaidFor(true);
          console.log("order approve" + order);
          console.log(data)
        },
        onError: err => {
          setError(err);
          console.error(err);
        },
      }).render(this.product.current);
  }, [product.description, product.price]);

  if (paidFor) {
    return (
      <div>
        <h1>Congrats, you just bought {product.name}!</h1>
        <img alt={product.description} src={gif} />
      </div>
    );
  }

  return (
  product.map(item => (
    <div>
      {error && <div>Uh oh, an error occurred! {error.message}</div>}
        <h1>{item.description} for ${item.price} </h1>
        <img alt={item.description} src={item.image} width="200" />
        {item.pp}
        <div ref={(node) => {item.pp = node;}}  />
    </div>
  ))
  );
}


function App() {
  const product = [
    {
      price: 0.01,
      name: 'stock 2',
      description: '1',
      image: chair,
      pp: paypalRef1,
    },
    {
      price: 0.01,
      name: 'stock2',
      description: '2',
      image: chair,
      pp: 'paypalRef2',
    },
    {
      price: 0.01,
      name: 'stock3',
      description: 'three',
      image: chair,
      pp: 'paypalRef3',
    }
  ];

  return (
    <div className="App">
      <Product product={product} />
    </div>
  );
}



export default App;
